  render() {
    const { user } = this.state;

    return (
      <ScrollView style={styles.wrapper}>
        <StatusBar backgroundColor={colors.green01} barStyle="light-content" />
        <View style={styles.welcomeWrapper}>
          {/* <Image
            source={greenlightLogo}
            style={styles.logo}
          /> */}
          <Text style={styles.welcomeText}>
            Welcome to AppName.
          </Text>
          <RoundedButton
            text="Continue with Email"
            textColor={colors.green01}
            background={colors.white}
            icon={<Icon name="envelope" size={20} style={styles.facebookButtonIcon} />}
            handleOnPress={this.onCreateAccountPress}
          />
          <RoundedButton
            text="Continue with Facebook"
            textColor={colors.green01}
            background={colors.white}
            icon={<Icon name="facebook" size={20} style={styles.facebookButtonIcon} />}
            handleOnPress={this.onFacebookPress}
          />
          <RoundedButton
            text="Continue with Google"
            textColor={colors.green01}
            background={colors.white}
            icon={<Icon name="google" size={20} style={styles.facebookButtonIcon} />}
